# display all polugged in drives
lsblk

echo .

# prompt user asking if they want to mount a drive
read -p "Would you like to mount a drive? (y/n): " mount_choice

case "$mount_choice" in
    [yY]|[yY][eE][sS])
        read -p "Enter the device name (e.g., /dev/sdX1): " device
        read -p "Enter the mount point (e.g., /mnt/mydrive): " mount_point
        sudo mkdir -p "$mount_point"
        sudo mount "$device" "$mount_point"
        echo "âœ… Mounted $device at $mount_point"
        ;;
    [nN]|[nN][oO])
        echo "ðŸš« No drive will be mounted."
        ;;
    *)
        echo "âš ï¸ Invalid choice. Please enter y or n."
        ;;
esac